'use strict';

function Hiyoco(weight) {
    this.weight = weight;
}

Hiyoco.prototype.feed = function(weight, callback) {
    if (weight < 0) {
        callback(new Error('invalid weight'));
    }
    else {
        this.weight += weight;
        callback(null);
    }
};

Hiyoco.prototype.canFly = function() {
    if (this.weight > 3) {
        return false;
    }
    else {
        return true;
    }
};

module.exports = Hiyoco;
