'use strict';

const assert = require('assert');
const Hiyoco = require('app/hiyoco');

describe('Hiyoco', () => {
    describe('feed', () => {
        it('weight: 0 -> 1', () => {
            const hiyoco = new Hiyoco(0);
            hiyoco.feed(1, () => {
                assert.strictEqual(hiyoco.weight, 1);
            });
        });
        it('weight: 2 -> 5 -> 10', () => {
            const hiyoco = new Hiyoco(2);
            hiyoco.feed(3, () => {
                assert.strictEqual(hiyoco.weight, 5);
                hiyoco.feed(5, () => {
                    assert.strictEqual(hiyoco.weight, 10);
                })
            });
        });
    });
    describe('canFly', () => {
        it('can - weight: 3', () => {
            const hiyoco = new Hiyoco(3);
            assert.strictEqual(hiyoco.canFly(), true);
        });
        it('can not - weight: 4', () => {
            const hiyoco = new Hiyoco(4);
            assert.strictEqual(hiyoco.canFly(), false);
        });
    });
});
